<?php
class Solution
{
    private $visited;
    private $size;

    private $queue;

    private $grid = [];
    private $directions = [
        [0, 1], [0, -1], [1, 0], [-1, 0]
    ];

    /**
     * @param Integer[][] $grid
     * @return Integer
     */
    function shortestBridge($grid)
    {
        $this->size = count($grid);
        $this->grid = $grid;
        $this->visited = [];
        $result = 0;

        for ($i = 0; $i < $this->size; $i++) {
            for ($j = 0; $j < $this->size; $j++) {
                if ($this->grid[$i][$j]) {
                    $this->dfs($i, $j);
                    break 2;
                }
            }
        }

        $queue = $this->queue;

        while (!empty($queue)) {
            $quantity = count($queue);

            for ($i = 0; $i < $quantity; $i++) {
                $node = array_shift($queue);

                foreach ($this->directions as $direction) {
                    $row = $node[0] + $direction[0];
                    $col = $node[1] + $direction[1];

                    if (
                        $this->isOutbound($row, $col)
                        || $this->wasVisited($row, $col)
                    ) {
                        continue;
                    }

                    if ($this->grid[$row][$col]) {
                        return $result;
                    }

                    $queue[] = [$row, $col];
                    $this->grid[$row][$col] = -1;
                }
            }

            $result++;
        }

        return $result;
    }

    function dfs($row, $col)
    {
        if (
            $this->isOutbound($row, $col)
            || $this->wasVisited($row, $col)
            || $this->grid[$row][$col] === 0
        ) {
            return;
        }

        $this->queue[] = [$row, $col];
        $this->grid[$row][$col] = -1;

        foreach ($this->directions as $direction) {
            $this->dfs(
                $row + $direction[0],
                $col + $direction[1],
            );
        }
    }

    function isOutbound($row, $col): bool
    {
        return $row < 0 || $col < 0 || $row >= $this->size || $col >= $this->size;
    }

    function wasVisited($row, $col): bool
    {
        return $this->grid[$row][$col] == -1;
    }
}

